INSERT INTO `aws_topic` VALUES ('4', '小米', '1351353973', '1', '小米公司正式成立于2010年4月，是一家专注于高端智能手机自主研发的移动互联网公司。\n 小米手机、MIUI、米聊是小米公司旗下三大核心业务。“为发烧而生”是小米的产品理念。小米公司首创了用互联网模式开发手机操作系统、60万发烧友参与开发改进的模式。', '20121028/c680403493157bf915b8ad9ddafa927a_32_32.png', '0', '9', '0', null);
